mapper.insert:

  	SELECT NVL(MAX(eat_no),0) + 1
  	FROM approvalstemp

   		INSERT INTO approvalstemp (
								eat_no
							   ,title
							   ,content
							   )
				VALUES         (
								 #{eatNo}
								,#{title}
								,#{content}
								)

mapper.update:
UPDATE approvalstemp
SET    title = #{title}
	   ,content = #{content}
WHERE eat_no = #{eatNo}

mapper.select:
SELECT emp_no, d.name, e.info
FROM employees 
WHERE name = #{empName}
