mapper.insert:

	SELECT NVL(MAX(eat_no),0) + 1
	FROM approvalstemp

INSERT INTO approvalstemp (
eat_no
,title
,content
.reg_date
)
VALUES (
#{eatNo}
,#{title}
,#{content}
,#{regDate}
)

mapper.update:
UPDATE approvalstemp
SET
title = #{title}
,content = #{content}
WHERE eat_no = #{eatNo}

mapper.insert:

	SELECT NVL(MAX(approvals.ea_no),0) + 1
	FROM approvals

INSERT INTO approvals(
approvals.ea_no
,title
,eat_no
,approvals.reg_date
,content
)
VALUES (
#{eaNo}
,#{title}
,#{eatNo}
,sysdate
,#{content}
)
