mapper.insert:

	SELECT NVL(MAX(post_no),1000) + 1
	FROM bods

INSERT INTO bods
(
post_no
, title
, content
, reg_date
, board_no
, writer
)
VALUES
(
#{postNo}
, #{title}
, #{content}
, sysdate
, #{boardNo}
,
#{writer}
)

mapper.update:
UPDATE bods
SET
title = #{title}
, content = #{content}
, board_no = #{boardNo}
, writer = #{writer}
, mod_date = sysdate
WHERE post_no = #{postNo}

