mapper.insert:

	SELECT NVL(MAX(sch_no), 0)+1
	FROM schs

INSERT INTO schs (
sch_no
,title
,start_Date
,end_date
,cal_no
,is_alarm)
VALUES (
#{schNo}
,#{title}
,#{startDate,jdbcType=TIMESTAMP}
,#{endDate,jdbcType=TIMESTAMP}
,#{calNo}
,#{isAlarm})



	SELECT NVL(MAX(cal_no), 0) + 1
	FROM cals


INSERT INTO cals (
cal_no
, name
, type
, reg_date
, is_delete
, delete_date
, color
)
VALUES (
#{calNo}
, #{name}
, #{type}
, SYSDATE
, #{isDelete}
, NULL
, #{color}
)

mapper.update:
UPDATE cals
SET
name = #{name},
color = #{color}
WHERE cal_no = #{calNo}
