mapper.select:
SELECT email
	, name
	, emp_no
FROM employees
WHERE email = #{email}
	AND password = #{password}

SELECT email
FROM employees
WHERE name = #{name}
	AND tel = #{tel}

SELECT emp_no
FROM employees
WHERE email = #{email}
	AND name = #{name}

mapper.update:
UPDATE employees
SET password = #{resetPwd}
WHERE email = #{email}

UPDATE employees


		tel = #{tel},


		info = #{info},


		password = #{password}


		img = #{profileImg}


WHERE emp_no = #{empNo}
