mapper.select:
SELECT * FROM ( SELECT e.emp_no AS empNo, e.name AS empName, e.tel, e.email, d.name AS deptName, j.name AS jobName, p.name AS posiName, e.work_type, ROW_NUMBER() OVER (ORDER BY e.join_date) AS rn FROM employees e LEFT JOIN departments d ON e.dept_no = d.dept_no LEFT JOIN jobs j ON e.job_no = j.job_no LEFT JOIN positions p ON e.posi_no = p.posi_no WHERE 1 = 1
AND d.name = #{deptFilter}
AND j.name = #{jobFilter}
AND p.name = #{posiFilter}
AND e.work_type = #{workTypeFilter}
) WHERE rn BETWEEN
(#{page} - 1) * 15 + 1 AND #{page} * 15

SELECT COUNT(e.emp_no)
FROM employees e
LEFT JOIN departments d ON
e.dept_no = d.dept_no
LEFT JOIN jobs j ON e.job_no = j.job_no
LEFT JOIN
positions p ON e.posi_no = p.posi_no
WHERE 1 = 1

	AND d.name = #{deptFilter}


	AND j.name = #{jobFilter}


	AND p.name = #{posiFilter}


	AND e.work_type = #{workTypeFilter}

SELECT * FROM ( SELECT e.emp_no, e.name AS empName, e.tel, e.email, e.name, d.name AS deptName, j.name AS jobName, p.name AS posiName,
CASE WHEN d.mgr_no = e.emp_no THEN 'Y' ELSE 'N' END AS isMgr, e.work_type, ROW_NUMBER() OVER (ORDER BY e.join_date) AS rn FROM employees e LEFT JOIN departments d ON e.dept_no = d.dept_no LEFT JOIN jobs j ON e.job_no = j.job_no LEFT JOIN positions p ON e.posi_no = p.posi_no WHERE 1 = 1
AND d.name = #{deptFilter}
AND j.name = #{jobFilter}
AND p.name = #{posiFilter}
AND e.work_type = #{workTypeFilter}
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + 15

mapper.update:
UPDATE employees
SET name = #{empName}
, tel = #{tel}
,
email = #{email}
, address = #{address}
, work_type = #{workType}
, info
= #{info}
, join_date = #{joinDate}
, leave_date = #{leaveDate}
, dept_no
= #{deptNo}
, job_no = #{jobNo}
, posi_no = #{posiNo}
, img = #{img}
WHERE emp_no = #{empNo}
