mapper.insert:

	SELECT NVL(MAX(proj_no), 1000)+1
	FROM proj

INSERT INTO proj(
proj_no
,name
,content
,start_date
,end_date
,is_template
,status
,proj_temp_no
,project_git_url
,p_mgr_no)
VALUES(
#{projNo}
,#{name}
,#{content}
,#{startDate}
,#{endDate}
,#{isTemplate}
,#{status}
,#{projTempNo}
,#{projectGitUrl}
,#{pMgrNo}
)

mapper.update:
UPDATE proj

SET
name = #{name}
,content = #{content}
,start_date =
#{startDate}
,end_date = #{endDate}

,status = #{status}

,project_git_url = #{projectGitUrl}
,p_mgr_no = #{pMgrNo}

WHERE proj_no
= #{projNo}
